mapper.delete:
DELETE FROM sample_data
WHERE monitoring_well_code = #{monitoringWellCode}
  AND sampling_time = #{samplingTime}

mapper.select:
SELECT sampling_time
FROM sample_data
WHERE monitoring_well_code = #{monitoringWellCode}
  AND sampling_time <= #{specifiedTime}
ORDER BY sampling_time DESC
LIMIT 1

SELECT sd.id, sd.monitoring_well_code, sd.sample_code, sd.sampling_time, sd.metric_code, pm.metric_name, sd.actual_test_value as value, pm.unit,
pm.metric_code as metrics_code, pm.metric_name as metrics_name FROM sample_data sd LEFT JOIN pollution_metric pm ON sd.metric_code = pm.metric_code WHERE sd.monitoring_well_code = #{monitoringWellCode} AND sd.sampling_time = #{samplingTime}
AND pm.metric_name IN
#{name}
ORDER BY sd.metric_code

SELECT sd.id, sd.monitoring_well_code, sd.sample_code, sd.sampling_time, sd.metric_code, pm.metric_name, sd.actual_test_value as value, pm.unit FROM sample_data sd LEFT JOIN pollution_metric pm ON sd.metric_code = pm.metric_code LEFT JOIN monitoring_well mw ON sd.monitoring_well_code = mw.well_code
AND sd.monitoring_well_code = #{monitoringWellCode}
AND sd.sampling_time >= #{startTime}
AND sd.sampling_time <= #{endTime}
AND mw.project_id = #{projectId}
ORDER BY sd.sampling_time DESC, sd.monitoring_well_code

SELECT DISTINCT
    sd.monitoring_well_code,
    sd.sampling_time,
    sd.sample_code,

    pm.metric_code as metrics_code,
    pm.metric_name as metrics_name
FROM sample_data sd
LEFT JOIN pollution_metric pm ON sd.metric_code = pm.metric_code
LEFT JOIN monitoring_well mw ON sd.monitoring_well_code = mw.well_code


        AND sd.monitoring_well_code = #{monitoringWellCode}


        AND sd.sampling_time >= #{startTime}


        AND sd.sampling_time <= #{endTime}


        AND mw.project_id = #{projectId}


ORDER BY sd.sampling_time DESC, sd.monitoring_well_code
LIMIT #{pageSize} OFFSET #{offset}

SELECT sd.id, sd.monitoring_well_code, sd.sample_code, sd.sampling_time, sd.metric_code, pm.metric_name, sd.actual_test_value as value, pm.unit,
pm.metric_code as metrics_code, pm.metric_name as metrics_name FROM sample_data sd LEFT JOIN pollution_metric pm ON sd.metric_code = pm.metric_code LEFT JOIN monitoring_well mw ON sd.monitoring_well_code = mw.well_code
AND sd.monitoring_well_code = #{monitoringWellCode}
AND sd.sampling_time >= #{startTime}
AND sd.sampling_time <= #{endTime}
AND mw.project_id = #{projectId}
ORDER BY sd.sampling_time DESC, sd.monitoring_well_code, sd.metric_code

SELECT COUNT(*) FROM (
    SELECT DISTINCT
        sd.monitoring_well_code,
        sd.sampling_time
    FROM sample_data sd
    LEFT JOIN monitoring_well mw ON sd.monitoring_well_code = mw.well_code


            AND sd.monitoring_well_code = #{monitoringWellCode}


            AND sd.sampling_time >= #{startTime}


            AND sd.sampling_time <= #{endTime}


            AND mw.project_id = #{projectId}


) AS subquery

SELECT
    id,
    monitoring_well_code,
    sample_code,
    sampling_time,
    testing_laboratory,
    metric_code,
    detection_limit,
    actual_test_value
FROM sample_data


        AND monitoring_well_code = #{sampleData.monitoringWellCode}


        AND sample_code = #{sampleData.sampleCode}


        AND testing_laboratory = #{sampleData.testingLaboratory}


        AND metric_code = #{sampleData.metricCode}


